package info.andersw.alpr;

import org.junit.Before;
import org.junit.jupiter.api.Test;
import org.springframework.beans.factory.annotation.Autowired;

import static org.junit.jupiter.api.Assertions.*;

class OpenAlprLibraryWrapperTest {

    String testdata = "data:image/jpeg;base64,";

    @Test
    void fileSuffixJPG() {
        OpenAlprLibraryWrapper wrapper = new OpenAlprLibraryWrapper();
        String suffix = wrapper.fileSuffix(testdata);
        assertEquals("jpeg", suffix);
    }

    @Test
    void fileSuffixPNG() {
        String pngTest = "data:image/png;base64,/9j/4";
        OpenAlprLibraryWrapper wrapper = new OpenAlprLibraryWrapper();
        String suffix = wrapper.fileSuffix(pngTest);
        assertEquals("png", suffix);
    }

    @Test
    void decodeImage() {
        OpenAlprLibraryWrapper wrapper = new OpenAlprLibraryWrapper();
        byte[] bytes = wrapper.decodeImage(testdata);
        assertEquals(3134, bytes.length);
    }
}